'''
A solution to a ROSALIND bioinformatics problem.

Problem Title: Translating RNA into Protein
Rosalind ID: PROT
Rosalind #: 008
URL: http://rosalind.info/problems/prot/
'''
def rna_to_protein(rna_string):
    # Define the RNA codon table
    codon_table = {
        "UUU": "F", "UUC": "F", "UUA": "L", "UUG": "L",
        "CUU": "L", "CUC": "L", "CUA": "L", "CUG": "L",
        "AUU": "I", "AUC": "I", "AUA": "I", "AUG": "M",
        "GUU": "V", "GUC": "V", "GUA": "V", "GUG": "V",
        "UCU": "S", "UCC": "S", "UCA": "S", "UCG": "S",
        "CCU": "P", "CCC": "P", "CCA": "P", "CCG": "P",
        "ACU": "T", "ACC": "T", "ACA": "T", "ACG": "T",
        "GCU": "A", "GCC": "A", "GCA": "A", "GCG": "A",
        "UAU": "Y", "UAC": "Y", "UAA": "*", "UAG": "*",
        "CAU": "H", "CAC": "H", "CAA": "Q", "CAG": "Q",
        "AAU": "N", "AAC": "N", "AAA": "K", "AAG": "K",
        "GAU": "D", "GAC": "D", "GAA": "E", "GAG": "E",
        "UGU": "C", "UGC": "C", "UGA": "*", "UGG": "W",
        "CGU": "R", "CGC": "R", "CGA": "R", "CGG": "R",
        "AGU": "S", "AGC": "S", "AGA": "R", "AGG": "R",
        "GGU": "G", "GGC": "G", "GGA": "G", "GGG": "G",
    }

    protein_string = ""
    
    # Iterate through the RNA string in chunks of 3 (codons)
    for i in range(0, len(rna_string), 3):
        codon = rna_string[i:i+3]
        
        # Check if the codon is in the codon table
        if codon in codon_table:
            amino_acid = codon_table[codon]
            
            # Stop translation if a stop codon is encountered
            if amino_acid == "*":
                break
            
            protein_string += amino_acid

    return protein_string

# Example usage:
rna_sequence = ""
result = rna_to_protein(rna_sequence)
print(result)
